<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">@</span><span style="color:#C792EA;">drag-bar</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#82AAFF;">onDragBar</span><span style="color:#A6ACCD;">($event</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">bar</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> $event</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">e)</span><span style="color:#89DDFF;">&quot;</span></span>
